package com.upthink.Canavas_QMS.dto.request;import com.upthink.Canavas_QMS.model.Course;import com.upthink.Canavas_QMS.dto.request.TokenValidation;import lombok.Getter;import lombok.Setter;import lombok.NoArgsConstructor;import lombok.AllArgsConstructor;@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class CreateCourseRequest extends AuthenticatedRequest {    private TokenValidation tokenValidationId;    private Course data;    public CreateCourseRequest(TokenValidation tokenValidationId, Course data) {        super(tokenValidationId);        this.tokenValidationId = tokenValidationId;        this.data = data;    }}