package com.upthink.Canavas_QMS.dto.request;import gson.GsonDTO;public class CreateEssayRequest extends GsonDTO {    private final String taskId;    private final String name;    private final String clientName;    private final String userId;    private final String uuid;    private final String dueDate;    private final int assignmentId;    public CreateEssayRequest(String taskId, String name, String clientName, String userId, String uuid, String dueDate, int assignmentId) {        this.taskId = taskId;        this.name = name;        this.clientName = clientName;        this.userId = userId;        this.uuid = uuid;        this.dueDate = dueDate;        this.assignmentId = assignmentId;    }    public String getTaskId() {        return taskId;    }    public String getName() {        return name;    }    public String getClientName(){        return clientName;    }    public String getUserId() {        return userId;    }    public String getUuid() {        return uuid;    }    public String getDueDate() {        return dueDate;    }    public int getAssignmentId() {        return assignmentId;    }}