package com.upthink.Canavas_QMS.model;import gson.GsonDTO;import jakarta.persistence.*;import java.sql.Timestamp;@Entity@Table(name="client")public class Client extends GsonDTO {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name="id", nullable = false)    private int id;    @Column(name="name", nullable = false, length = 255)    private String name;    @Column(name="created_at")    private Timestamp createdAt;    @Column(name="updated_at")    private Timestamp updatedAt;    @Column(name="deleted_at")    private Timestamp deletedAt;    protected Client() {}    public int getId() {        return id;    }    public String getName() {        return name;    }    public Timestamp getCreatedAt() {        return createdAt;    }    public Timestamp getUpdatedAt() {        return updatedAt;    }    public Timestamp getDeletedAt() {        return deletedAt;    }}