package com.upthink.Canavas_QMS.model;import gson.GsonDTO;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.Table;import java.sql.Timestamp;@Entity@Table(name = "course")public class Course extends GsonDTO {    @Id    @Column(name = "id", nullable = false)    private String id;    @Column(name = "client_id", nullable = false)    private int clientId;    @Column(name = "name", nullable = false)    private String name;    @Column(name="sla", nullable = false, columnDefinition="bigint default 48")    private long sla;    @Column(name="created_at", nullable = false)    private Timestamp createdAt;    @Column(name="updated_at")    private Timestamp updatedAt;    @Column(name="deleted_at")    private Timestamp deletedAt;    protected Course() {}    public String getId() {        return id;    }    public int getClientId() {        return clientId;    }    public String getName() {        return name;    }    public Timestamp getCreatedAt() {        return createdAt;    }    public Timestamp getUpdatedAt() {        return updatedAt;    }    public Timestamp getDeletedAt() {        return deletedAt;    }}