package com.upthink.Canavas_QMS.service;import com.upthink.Canavas_QMS.model.Assignment;import com.upthink.Canavas_QMS.repository.AssignmentRepository;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.sql.Timestamp;import java.util.List;import java.util.Optional;@Servicepublic class AssignmentService {    private final AssignmentRepository assignmentRepository;    public AssignmentService(AssignmentRepository assignmentRepository) {        this.assignmentRepository = assignmentRepository;    }    public List<Assignment> getAllAssignments() {        return assignmentRepository.findAllAssignments();    }    public Optional<Assignment> getAssignmentById(int id) {        return assignmentRepository.findAssignmentById(id);    }    @Transactional    public void saveAssignment(Assignment assignment) {        assignmentRepository.insertAssignment(                assignment.getCourseId(),                assignment.getCreatedAt(),                assignment.getUpdatedAt(),                assignment.getDeletedAt(),                assignment.getDueDate(),                assignment.getLockDate(),                assignment.getGradeType(),                assignment.getPointsPossible(),                assignment.getRubricBasedGrading(),                assignment.getRubric(),                assignment.getSubmissionStarted(),                assignment.getAssignmentId()        );    }    @Transactional    public void deleteAssignment(int id) {        assignmentRepository.deleteAssignmentById(id);    }}