package com.upthink.Canavas_QMS.service;import com.upthink.Canavas_QMS.dto.response.EssayClientResponse;import com.upthink.Canavas_QMS.repository.EssayClientRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class EssayClientService {    EssayClientRepository essayClientRepository;    @Autowired    public EssayClientService (EssayClientRepository essayClientRepository) {        this.essayClientRepository = essayClientRepository;    }    public EssayClientResponse loadEssayClientByName(String name) {        Optional<EssayClient> essayClientOpt = essayClientRepository.findByName(name);    }}