package com.upthink.Canavas_QMS.service;import com.upthink.Canavas_QMS.model.EssayDetails;import com.upthink.Canavas_QMS.repository.EssayDetailsRepository;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.sql.Timestamp;import java.util.List;import java.util.Optional;@Servicepublic class EssayDetailsService {    private final EssayDetailsRepository essayDetailsRepository;    public EssayDetailsService(EssayDetailsRepository essayDetailsRepository) {        this.essayDetailsRepository = essayDetailsRepository;    }    public List<EssayDetails> getAllEssayDetails() {        return essayDetailsRepository.findAllEssayDetails();    }    public Optional<EssayDetails> getEssayDetailsById(int id) {        return essayDetailsRepository.findEssayDetailsById(id);    }    @Transactional    public void saveEssayDetails(EssayDetails essayDetails) {        essayDetailsRepository.insertEssayDetails(                essayDetails.getPersonId(),                essayDetails.getEssayId(),                essayDetails.getUserAction(),                essayDetails.getCreatedAt(),                essayDetails.getUpdatedAt(),                essayDetails.getDeletedAt(),                essayDetails.getDownloadedAt()        );    }    @Transactional    public void deleteEssayDetails(int id) {        essayDetailsRepository.deleteEssayDetailsById(id);    }}