package com.upthink.Canavas_QMS.service;import com.upthink.Canavas_QMS.model.Essay;import com.upthink.Canavas_QMS.repository.EssayRepository;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.math.BigDecimal;import java.sql.Timestamp;import java.util.List;import java.util.Optional;@Servicepublic class EssayService {    private final EssayRepository essayRepository;    public EssayService(EssayRepository essayRepository) {        this.essayRepository = essayRepository;    }    public List<Essay> getAllEssays() {        return essayRepository.findAllEssays();    }    public Optional<Essay> getEssayById(int id) {        return essayRepository.findEssayById(id);    }    @Transactional    public void saveEssay(Essay essay) {        essayRepository.insertEssay(                essay.getEssayId(),                essay.getName(),                essay.getSubmissionId(),                essay.getAssignmentId(),                essay.getUserId(),                essay.getUngradedFileLink(),                essay.getStatus(),                essay.getGradeTime(),                essay.getFetchedFor(),                essay.getGradedFileLink(),                essay.getCreatedAt(),                essay.getUpdatedAt(),                essay.getExpiresAt(),                essay.getDeletedAt(),                essay.getFinalGrade(),                essay.getGradeOutOf(),                essay.getRubric(),                essay.getComments(),                essay.getToPush(),                essay.getPushStatus()        );    }    @Transactional    public void deleteEssay(int id) {        essayRepository.deleteEssayById(id);    }}