package com.upthink.Canavas_QMS.service;import com.upthink.Canavas_QMS.model.Person;import com.upthink.Canavas_QMS.repository.PersonRepository;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;import java.util.Optional;@Servicepublic class PersonService {    private final PersonRepository personRepository;    public PersonService(PersonRepository personRepository) {        this.personRepository = personRepository;    }    public List<Person> getAllPersons() {        return personRepository.findAllPersons();    }    public Optional<Person> getPersonById(String id) {        return personRepository.findPersonById(id);    }    @Transactional    public void savePerson(Person person) {        personRepository.insertPerson(                person.getId(),                person.getName(),                person.getEmail(),                person.getCognitoId(),                person.getGroups(),                person.isActive(),                person.getPhoneNumber(),                person.getUserName()        );    }    @Transactional    public void deletePerson(String id) {        personRepository.deletePersonById(id);    }}