package com.upthink.qms.dto.request;import java.util.List;public class GetAllEssaysRequest extends AuthenticatedRequest {    private final List<String> essayStatusList;    private final int clientId;    public GetAllEssaysRequest(TokenValidation tokenValidationId, List<String> essayStatusList) {        super(tokenValidationId);        this.essayStatusList = essayStatusList;        this.clientId = 0;    }    public GetAllEssaysRequest(TokenValidation tokenValidationId, List<String> essayStatusList, int clientId) {        super(tokenValidationId);        this.essayStatusList = essayStatusList;        this.clientId = clientId;    }    public List<String> getEssayStatusList() {        return essayStatusList;    }    public int getClientId() {        return clientId;    }}