package com.upthink.qms.dto.response;import gson.GsonDTO;import java.util.List;public class AssignmentResponse extends GsonDTO {    private final List<AssignmentDTO> assignmentDTOList;    public AssignmentResponse(List<AssignmentDTO> assignmentDTOList) {        this.assignmentDTOList = assignmentDTOList;    }    public List<AssignmentDTO> getAssignmentDTOList() {        return assignmentDTOList;    }    public static class AssignmentDTO extends GsonDTO{        private final int id;        private final String assignmentId;        public AssignmentDTO(int id, String assignmentId) {            this.id = id;            this.assignmentId = assignmentId;        }        public int getId() {            return id;        }        public String getAssignmentId() {            return assignmentId;        }    }}