package com.upthink.qms.model;import gson.GsonDTO;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.Table;import jakarta.persistence.Transient;import java.util.List;@Entity@Table(        name = "person")public class Person extends GsonDTO {    @Id    @Column(            name = "id",            nullable = false    )    private String id;    @Column(            name = "name",            nullable = false    )    private String name;    @Column(            name = "email",            nullable = false,            unique = true    )    private String email;    @Column(            name = "cognitoid",            nullable = false,            unique = true    )    private String cognitoId;    @Column(            name = "groups",            columnDefinition = "varchar[]"    )    private List<String> groups;    @Column(            name = "active",            nullable = false    )    private boolean active;    @Transient    private String phoneNumber;    @Transient    private String userName;    protected Person() {    }    public Person(String id, String cognitoId, String name, String email, List<String> groups, boolean active) {        this.id = id;        this.cognitoId = cognitoId;        this.name = name;        this.email = email;        this.groups = groups;        this.active = active;    }    public String getId() {        return this.id;    }    public String getCognitoId() {        return this.cognitoId;    }    public String getName() {        return this.name;    }    public String getEmail() {        return this.email;    }    public List<String> getGroups() {        return this.groups;    }    public boolean isActive() {        return this.active;    }    public void setGroups(List<String> groups) {        this.groups = groups;    }    public void setActive(boolean active) {        this.active = active;    }}